            `https://api.unsplash.com/search/photos?page=1&orientation=landscape&query=${
                term || "landscape"
            }&client_id=36SRg3AU2MG6AdJcThKvHnoNLUbDIrDN0ORiB19hNH8`
        );
        photos = response.data.results;
    };
    const handleSearch = async () => {
        if (!term) return
        await fetchData()
        term = ''
    }

</script>


