        {#each photos as photo, i (photo.id)}
            <a href={photo.urls.regular} target="_blank" in:fly={{y:200, duration: 2000, delay: i * 200}}>
                <img class="image" src={photo.urls.regular} alt={photo.alt_description}>
            </a>
        {/each}
    </div>
</div>

<style lang="scss">

  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Roboto", sans-serif;
    font-weight: bold;

  }

  .photos {
    display: grid;
    width: 100%;
    grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
    grid-gap: 1rem;

    img {
      width: 100%;
      border-radius: 8px;
      height: 240px;
      box-shadow: 1px 1px 8px rgba(0, 0, 0, 0.23);
      transition: all 0.25s ease-in-out;

      &:hover {
        cursor: pointer;
        transition: all 0.25s ease-in-out;
        scale: 0.95;
      }
    }
  }

  .container {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    width: 100%;
    height: 100%;
    padding: 1rem;
  }

  .header {
    margin: 1rem;
    text-align: center;
    font-size: 2rem;
    width: 100%;

    .svg-header {
      width: 80%;
      animation: headerAnim 5s ease-in-out infinite;
    }

    .input-container {
      margin: 1rem auto;
      width: 100%;
      display: flex;
      justify-content: center;
      align-items: center;
    }
  }

  .input {
    margin-right: 1rem;
    min-width: 220px;
    width: 400px;
    padding: 0.5rem 0.75rem;
    border-radius: 10px;
    outline: none;
    border: none;
    box-shadow: 1px 1px 8px rgba(0, 0, 0, 0.23);
    font-size: 1.25rem;
    font-weight: normal;

    &::placeholder {
      font-size: 1rem;
      font-weight: normal;
    }
  }

  .button {
    padding: 0.5rem 1rem;
    font-size: 20px;
    background-color: #202020;
    border-radius: 10px;
    color: white;
    outline: none;
    border: none;
    transition: all 0.2s ease-in-out;
    box-shadow: 1px 1px 8px rgba(0, 0, 0, 0.23);

    &:hover:enabled {
      cursor: pointer;
      transition: all 0.2s ease-in-out;
      scale: 0.95;
    }

    &:disabled {
      background-color: gray;
    }
  }

  .input-container {
    margin-bottom: 40px;
  }
</style>

